import React, { useState } from "react";
const DarkLight = () => {
  const [bgcolor, setBgcolor] = useState("#f1f1f1");
  function setDark() {
    setBgcolor("#000");
  }
  function setLight() {
    setBgcolor("#fff");
  }
  return (
    <div className="row vh-100" style={{ backgroundColor: `${bgcolor}` }}>
      <div className="col-md-12  d-flex  justify-content-center text-white  align-items-center">
        <button className="btn btn-basic" onClick={() => setDark()}>
          Light
        </button>
        <button className="btn btn-basic" onClick={() => setLight()}>
          Dark
        </button>
      </div>
    </div>
  );
};
export default DarkLight;
